#include "../include/header.h"

class Solution {
public:
    string minWindow(string s, string t) {
        int min_length = INT_MAX;
        int i = 0, j = 0;
        unordered_map<char, int> window, need;
        int valid = 0;
        string result = "";
        int result_l = -1;
        // string temp_result = "";
        // deque<char> que;
        for(char& c : t){
            need[c]++;
        }
        while(j < s.size()){
            
            //扩张
            window[s[j]]++;
            // temp_result.append(s[j]);
            // que.push_back(s[j]);
            

            if(need.find(s[j]) != need.end()){
                if(window[s[j]] == need[s[j]]){
                    valid++;
                }
            }

            //条件满足缩小，并且记录最小包含的窗口 
            while(valid == need.size()){
                // min_length = min(min_length, j - i + 1);
                if(min_length > j - i + 1){
                    // FIXME: leetcode 出现内存限制的问题，本地没有
                    // result = string(s.begin() + i, s.begin() + j + 1 );
                    result_l = i;
                    min_length = j - i + 1;
                }
                window[s[i]]--;
                // que.pop_front();
                auto itr = need.find(s[i]); 
                if(itr != need.end()){ //属于需要元素 判断window中数量是否满足需求
                    if(window[s[i]] < itr->second){
                        valid--;
                    } 
                }
                i++;
            }
            
            j++;

        }
        result = result_l == -1? string() :  s.substr(result_l, min_length);

        return result;
    }
};

int main(){
    string s = "";
    string t = "aAbBcCdDeEfFgGhHiIjJkKlLmMnNoOpPqQrRsStTuUvVwWxXyYzZ";
    
    // string result;
    Solution solution;
    cout << solution.minWindow(s, t) << endl;
    
    return 0;
}